import { useState, useEffect } from 'react'
import './App.css'
import Client from './services/api'
import NavBar from './components/NavBar'
import ReviewForm from './components/ReviewForm'
import AllProducts from './pages/AllProducts'
import ProductDetails from './pages/ProductDetails'
import Home from './pages/Home'
import About from './pages/About'
import Register from './pages/Register'
import SignIn from './pages/SignIn'
import { Route, Routes } from 'react-router'
import { CheckSession } from './services/User'

const App = () => {
  const [user, setUser] = useState(null)
  const [products, setProducts] = useState([])
  const [searchResults, setSearchResults] = useState([])
  const [searched, toggleSearched] = useState(false)
  const [searchQuery, setSearchQuery] = useState('')
  const [reviews, setReviews] = useState([])

  // const handleLogOut = () => {
  //   setUser(null)
  //   localStorage.clear()
  // }

  const checkToken = async () => {
    const user = await CheckSession()
    setUser(user)
  }

  useEffect(() => {
    const token = localStorage.getItem('token')

    if (token) {
      checkToken()
    }
  }, [])

  // const [account, setAccount] = useState({})

  // useEffect(() => {
  //   if (user) {
  //     const handleAccount = async () => {
  //       const data = await Client.get(`/api/users/info/${user.id}`)
  //       setAccount(data.data)
  //     }
  //     handleAccount()
  //   }
  // }, [user, setAccount])

  const getProducts = async () => {
    const response = Client.get(`/api/products`)
    console.log('HELLO')
    console.log(response.data)
    setProducts(response.data.destinations)
    let reviews = response.data.reviews
    setReviews(reviews)
  }

  useEffect(() => {
    getProducts()
  }, [])

  const getSearchResults = async (e) => {
    e.preventDefault()
    const response = await Client.get(`/api/products/${searchQuery}`)
    console.log('SEARCH')
    console.log(response)
    setSearchResults(response.data.results)
    toggleSearched(true)
    setSearchQuery('')
  }

  const handleChange = (event) => {
    setSearchQuery(event.target.value)
  }

  return (
    <div className="App">
      <div>
        <NavBar />
      </div>
      <main>
        <Routes>
          <Route path="/" element={<Home products={products} />} />
          <Route path="/about" element={<About />} />
          <Route path="/signIn" element={<SignIn setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/allProducts"
            element={<AllProducts products={products} />}
          />
          <Route path="/details" element={<ProductDetails />} />
          <Route
            path="/form/:userId/:productId"
            element={<ReviewForm user={user} />}
          />
        </Routes>
      </main>
    </div>
  )
}

export default App
