// import { Link } from 'react-router-dom'
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const ProductDetails = ({ allProducts }) => {
  let { id } = useParams()
  console.log(id)
  const [details, setDetails] = useState()

  const result = allProducts.filter((product) => product.id === id)
  useEffect(() => {
    setDetails(result)
  }, [allProducts])
  console.log(result)
  console.log(details)
  return (
    <div className="product-card">
      <div className="img-wrapper">
        <img alt={result.name} src={details.image} />
        <div className="product-info">
          <h3>Name: {result.name}</h3>
          <h3>Price: ${result.price}</h3>
          <h3>Details: {result.description}</h3>
          {/* <p>Reviews: {props.reviews.content}</p> */}
        </div>
      </div>
      <div>
        <button>Add to Cart</button>
      </div>
    </div>
  )
}

export default ProductDetails
