import Client from '../services/api'
import { Link, useNavigate } from 'react-router-dom'
import Search from '../components/Search'
// import ProductCard from '../components/ProductCard'
import { useState, useEffect } from 'react'

const Home = (props) => {
  let navigate = useNavigate()
  const [featured, setFeatured] = useState({})
  const [searchResults, setSearchResults] = useState([])
  const [searched, toggleSearched] = useState(false)
  const [searchQuery, setSearchQuery] = useState('')

  useEffect(() => {
    const getFeatured =
      props.products[Math.floor(Math.random() * props.products.length)]

    setFeatured(getFeatured)
  }, [props.products])

  const getSearchResults = async (e) => {
    e.preventDefault()
    const response = await Client.get(`/api/products/${searchQuery}`)
    console.log('SEARCH')
    console.log(response)
    setSearchResults(response.data.results)
    toggleSearched(true)
    setSearchQuery('')
  }

  const handleChange = (event) => {
    setSearchQuery(event.target.value)
  }

  return (
    <div className="nav-left">
      <div>
        <Search
          onSubmit={getSearchResults}
          onChange={handleChange}
          value={searchQuery}
        />
        {searched ? (
          <div className="search">
            <h2>Search Results</h2>
            <section className="example">
              {searchResults.map((result) => (
                <Link to={`/productDetails/${result.id}`} key={result.id}>
                  <div>
                    <img alt="" src={result.image} />
                    <h3>{result.name}</h3>
                    <p>${result.price}</p>
                    <p>{result.description}</p>
                  </div>
                </Link>
              ))}
            </section>
          </div>
        ) : (
          <div className="products">
            <section className="container-grid">
              <Link to="/allProducts">
                <img className="coverImage" src={featured.image} alt="Cover" />
              </Link>
              <button onClick={() => navigate('/allProducts')}>
                SHOP BELLA LUNA
              </button>
            </section>
          </div>
        )}
      </div>

      <div>{/* <h1>Bella Luna Designs</h1> */}</div>
    </div>
  )
}

export default Home
